import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axiosInstance from '../config';
import { useUtiliserPanier } from '../contextes/ContextePanier';
import WishlistButton from '../components/WishlistButton';
import '../styles/Accueil.css';

const Accueil = () => {
  const [produits, setProduits] = useState([]);
  const [chargement, setChargement] = useState(true);
  const { ajouterAuPanier } = useUtiliserPanier();

  useEffect(() => {
    chargerProduits();
  }, []);

  const chargerProduits = async () => {
    try {
      const response = await axiosInstance.get('/api/products', { params: { per_page: 8 } });
      setProduits(response.data.data);
    } catch (error) {
      console.error('Erreur lors du chargement des produits:', error);
    } finally {
      setChargement(false);
    }
  };

  const gererAjoutPanier = (produit) => {
    ajouterAuPanier(produit);
    alert(`${produit.nom} ajoutÃ© au panier !`);
  };

  if (chargement) {
    return <div className="chargement">Chargement...</div>;
  }

  return (
    <div className="conteneur">
      {/* Section Hero */}
      <section className="hero">
        <div className="hero-contenu">
          <div className="hero-badge">ð­ Mode DÃ©mo</div>
          <h1>Bienvenue sur notre E-Commerce</h1>
          <p className="sous-titre-hero">
            DÃ©couvrez nos produits de qualitÃ© Ã  des prix exceptionnels
          </p>
          <div className="hero-actions">
            <Link to="/produits" className="btn btn-primaire btn-hero">
              Voir tous les produits
            </Link>
            <Link to="/wishlist" className="btn btn-secondaire btn-hero">
              â¤ï¸ Ma Wishlist
            </Link>
          </div>
        </div>
      </section>

      {/* Produits vedette */}
      <section className="produits-vedette">
        <h2>Produits Vedette</h2>
        <div className="grille-produits">
          {produits.map(produit => (
            <div key={produit.id} className="carte-produit">
              <div className="conteneur-image-produit">
                <WishlistButton 
                  productId={produit.id} 
                  className="position-absolue petit"
                />
                
                <Link to={`/produits/${produit.id}`}>
                  {produit.images && produit.images[0] ? (
                    <img 
                      src={produit.images[0]} 
                      alt={produit.nom}
                      className="image-produit"
                    />
                  ) : (
                    <div className="placeholder-image">
                      <span>Aucune image</span>
                    </div>
                  )}
                </Link>

                {produit.en_promotion && (
                  <div className="badge-promo">
                    -{Math.round(((produit.prix - produit.prix_promo) / produit.prix) * 100)}%
                  </div>
                )}
              </div>
              
              <div className="contenu-carte-produit">
                <Link to={`/produits/${produit.id}`} className="nom-produit">
                  {produit.nom}
                </Link>
                
                {produit.marque && (
                  <div className="marque-produit">{produit.marque}</div>
                )}

                <p className="description-produit">{produit.description}</p>
                
                {produit.note_moyenne > 0 && (
                  <div className="note-produit">
                    <div className="etoiles">
                      {[...Array(5)].map((_, i) => (
                        <span 
                          key={i} 
                          className={`etoile ${i < Math.round(produit.note_moyenne) ? 'pleine' : 'vide'}`}
                        >
                          â
                        </span>
                      ))}
                    </div>
                    <span className="nombre-avis">({produit.nombre_avis})</span>
                  </div>
                )}
                
                <div className="info-produit">
                  <div className="prix-section">
                    {produit.en_promotion ? (
                      <>
                        <span className="prix-promo">{produit.prix_final}â¬</span>
                        <span className="prix-original">{produit.prix}â¬</span>
                      </>
                    ) : (
                      <span className="prix-produit">{produit.prix_final}â¬</span>
                    )}
                  </div>
                  
                  <button 
                    onClick={() => gererAjoutPanier(produit)}
                    className="btn-ajouter-panier"
                    disabled={produit.stock <= 0}
                  >
                    {produit.stock > 0 ? 'Ajouter au panier' : 'Rupture de stock'}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Section statistiques */}
      <section className="stats-accueil">
        <div className="stat-item">
          <span className="stat-nombre">1000+</span>
          <span className="stat-label">Produits</span>
        </div>
        <div className="stat-item">
          <span className="stat-nombre">5000+</span>
          <span className="stat-label">Clients satisfaits</span>
        </div>
        <div className="stat-item">
          <span className="stat-nombre">24h</span>
          <span className="stat-label">Livraison rapide</span>
        </div>
        <div className="stat-item">
          <span className="stat-nombre">4.8/5</span>
          <span className="stat-label">Note moyenne</span>
        </div>
      </section>
    </div>
  );
};

export default Accueil;
