import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axiosInstance from '../config';
import { useUtiliserPanier } from '../contextes/ContextePanier';
import WishlistButton from '../components/WishlistButton';
import '../styles/Accueil.css';

const Accueil = () => {
  const [produits, setProduits] = useState([]);
  const [chargement, setChargement] = useState(true);
  const { ajouterAuPanier } = useUtiliserPanier();

  useEffect(() => {
    chargerProduits();
  }, []);

  const chargerProduits = async () => {
    try {
      const response = await axiosInstance.get('/api/products', { params: { per_page: 8 } });
      setProduits(response.data.data);
    } catch (error) {
      console.error('Erreur lors du chargement des produits:', error);
    } finally {
      setChargement(false);
    }
  };

  const gererAjoutPanier = (produit) => {
    ajouterAuPanier(produit);
    alert(`${produit.nom} ajoutÃ© au panier !`);
  };

  if (chargement) {
    return <div className="chargement">Chargement...</div>;
  }

  return (
    <div className="conteneur">
      {/* Section Hero */}
      <section className="hero">
        <div className="hero-contenu">
          <div className="hero-badge">
            â¨ Nouveau â¢ Livraison gratuite dÃ¨s 50â¬
          </div>
          <h1>DÃ©couvrez l'Excellence</h1>
          <p className="sous-titre-hero">
            Une sÃ©lection premium de produits soigneusement choisis pour vous offrir 
            la meilleure expÃ©rience shopping en ligne
          </p>
          <div className="hero-actions">
            <Link to="/produits" className="btn btn-primaire btn-hero">
              <span>ðï¸</span>
              Explorer la boutique
            </Link>
            <Link to="/wishlist" className="btn btn-secondaire btn-hero">
              <span>â¤ï¸</span>
              Ma Wishlist
            </Link>
          </div>
        </div>
        <div className="hero-decoration">
          <div className="floating-element element-1"></div>
          <div className="floating-element element-2"></div>
          <div className="floating-element element-3"></div>
        </div>
      </section>

      {/* Section Avantages */}
      <section className="avantages">
        <div className="avantage-item">
          <div className="avantage-icone">ð</div>
          <h3>Livraison Rapide</h3>
          <p>Livraison en 24h partout en France</p>
